        		pcm = entry.link[k].title.split(" ")[0];
        		break;
      		}
    	}

    	if ("content" in entry) {
      		var postcontent = entry.content.$t;}
    	else
    	if ("summary" in entry) {
      		var postcontent = entry.summary.$t;}
    	else var postcontent = "";

    	postdate = entry.published.$t;

	if(j>imgr.length-1) j=0;
	img[i] = imgr[j];

